mapper.update:
update sell set member_no = #{member_no}, order_no =
#{order_no}, day =
#{day} where no = #{no}

mapper.select:
		select count(*)
        from(
            select * 
            from(
                select m.no, m.name member_name, c.name client_name, m.DAY
                from(select s.no, m.name, s.client_no, s.DAY
                     from sell s inner join member m
                     on s.member_no = m.no) m inner join client c
                on m.client_no = c.no
            ) where no > 0

             and member_name like '%' ||  #{keyword} || '%' 
             and client_name like '%' ||  #{keyword} || '%' 


        )
