mapper.update:
update order_sheet 
set member_no=#{member_no}, client_no=#{client_no}, out_day=#{out_day}
where no=#{no}

mapper.select:
		select count(*)
        from(
            select * 
            from(
                select m.no, m.name member_name, c.name client_name, m.day, m.out_day, m.status
                from(select o.no, m.name, o.client_no, o.day, o.out_day, o.status
                     from order_sheet o inner join member m
                     on o.member_no = m.no) m inner join client c
                on m.client_no = c.no
            ) where no > 0

             and member_name like '%' ||  #{keyword} || '%' 
             and client_name like '%' ||  #{keyword} || '%' 

        )

select no, order_no, item_no, item_code, item_name, in_price, out_price, amount, client_no, client_code, client_name
from(
	select o.no, o.order_no, i.no item_no, i.code item_code, i.name item_name,in_price, out_price, o.amount, c.no client_no, c.code client_code, c.name client_name
	from order_detail o inner join item i
	on o.item_no = i.no
	inner join client c
	on i.client_no = c.no
)where order_no = #{mainNo}

select * from ( select * from( select rownum rn, p.no , p.day, p.OUT_DAY, p.status, c.no as client_no, c.name as client_name, m.no as Member_no, m.name as member_name from order_sheet p, Client c, Member m where p.client_no = c.no(+) and p.member_no = m.no(+) order by no desc) where rownum <= #{cri.pageNum} * #{cri.amount}
and member_name like '%' || #{cri.keyword} || '%' and client_name like '%' || #{cri.keyword} || '%'
and no in
#{arr}
)where rn > (#{cri.pageNum} - 1) * #{cri.amount} and status = 0

select count(*) from( select rownum rn, p.no , p.day, p.OUT_DAY, p.status, c.no as client_no, c.name as client_name, m.no as Member_no, m.name as member_name from order_sheet p, Client c, Member m where p.client_no = c.no(+) and p.member_no = m.no(+) order by no desc) where status = 0 and member_name like '%' || #{cri.keyword} || '%' and client_name like '%' || #{cri.keyword} || '%'
and no in
#{arr}

mapper.update:
update ORDER_SHEET
set status = #{status}
where no = #{ordersheetno}
